var Client   = require('../src').Client;
var clearbit = new Client();

clearbit.Prospector.search({domain: 'clearbit.com'})
  .then(function (results) {
    console.log('Results:', results);

    results[0].getEmail().then(function(email){
      console.log(email);
    });
  })
  .catch(function (err) {
    console.error(err);
  });

